import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import axios from 'axios';

//Api Key 8d41633b8fdaee84acd555ef911ff211
//Api Read Access TokeneyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI4ZDQxNjMzYjhmZGFlZTg0YWNkNTU1ZWY5MTFmZjIxMSIsInN1YiI6IjY1Mjk5YWU3MWYzZTYwMDBhYzRkODExZiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.eClhzdu5OPTFJvvTOKpoetCAbfc2vVRQA2cNniX3m00



const MovieList = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.themoviedb.org/3/movie/top_rated', {
          headers: {
            'Authorization': '8d41633b8fdaee84acd555ef911ff211', // Replace with your API key
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const result = await response.json();
          setMovies(result);
          console.log(movies);
        } else {
          console.error('Error fetching data:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  return (
    <div>
      <div>hello</div>
      <h2>Movie List</h2>
      <ul>
        {movies.map(movie => (
          <li key={movie.id}>
            <NavLink to={`/movies/${movie.id}`}>{movie.title}</NavLink>
          </li>
        ))}
      </ul>
    </div>
  );
};

const MovieDetail = ({ match }) => {
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    // Fetch movie details from an API (replace with your API endpoint)
    axios.get(`https://api.themoviedb.org/3/movie/{movie_id}`)
      .then(response => setMovie(response.data))
      .catch(error => console.error('Error fetching movie details:', error));
  }, [match.params.id]);

  if (!movie) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      Hello 
      <h2>{movie.title}</h2>
      <p>{movie.description}</p>
      {/* Add additional movie details here */}
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li><NavLink to="/">Home</NavLink></li>
            <li><NavLink to="/movies">Movies</NavLink></li>
          </ul>
        </nav>
        <hr />
        <Routes>
          <Route path="/movies/:id" element={MovieDetail} />
          <Route path="/movies" component={MovieList} />
          <Route path="/" exact>
          </Route>
        </Routes>
      </div>
    </Router>
  );
};

export default App;
