import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom';
import axios from 'axios';

const MovieList = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    // Fetch movies from an API (replace with your API endpoint)
    axios.get('https://api.example.com/movies')
      .then(response => setMovies(response.data))
      .catch(error => console.error('Error fetching movies:', error));
  }, []);

  return (
    <div>
      <h2>Movie List</h2>
      <ul>
        {movies.map(movie => (
          <li key={movie.id}>
            <Link to={`/movies/${movie.id}`}>{movie.title}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

const MovieDetail = ({ match }) => {
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    // Fetch movie details from an API (replace with your API endpoint)
    axios.get(`https://api.example.com/movies/${match.params.id}`)
      .then(response => setMovie(response.data))
      .catch(error => console.error('Error fetching movie details:', error));
  }, [match.params.id]);

  if (!movie) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{movie.title}</h2>
      <p>{movie.description}</p>
      {/* Add additional movie details here */}
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/movies">Movies</Link></li>
          </ul>
        </nav>
        <hr />
        <Switch>
          <Route path="/movies/:id" component={MovieDetail} />
          <Route path="/movies" component={MovieList} />
          <Route path="/" exact>
            <h2>Home</h2>
            <p>Welcome to the Movie Rating App!</p>
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
